function gadget:GetInfo()  return {    name      = "collectible features",    desc      = "driving over some features heals the unit",    author    = "knorke",    date      = "Nov 2012",    license   = "horse has fallen over",    layer     = -3,    enabled   = true  }endlocal GaiaTeamID  = Spring.GetGaiaTeamID ()local healthFeature =  FeatureDefNames["f_tprepaircrate"].idfunction gadget:FeatureDestroyed(featureID, allyTeam)		if (Spring.GetFeatureDefID (featureID) == healthFeature) then		local x,y,z = Spring.GetFeaturePosition (featureID)				local units = Spring.GetUnitsInCylinder (x,z, 100)				local healBonus = 1000		for i,unitID in pairs(units) do			local health, maxHealth = Spring.GetUnitHealth (unitID)			local diff = maxHealth - health						heal = math.min(diff, healBonus)						healBonus = healBonus - heal						Spring.SetUnitHealth (unitID, health+heal)			Spring.Echo ("healing " .. unitID .. " by " .. heal)			env = Spring.UnitScript.GetScriptEnv(unitID)			if (env) then Spring.UnitScript.CallAsUnit(unitID, env.healthRegained, heal) end						if heal < 0 then return end						return --remove to allow healing more than one unit					end					end	endfunction gadget:GameFrame (f)--Spring.Echo (treeFeature)end