function widget:GetInfo()	return {	name = "Brickcraft | automatic capture on rightclick",	desc = "easier sirup pump capturing",	author = "knorke",	date = "2012",	license = "horse",	layer = 1,	enabled = true	}endlocal autoCaptureUnits = {	[UnitDefNames["tppioneer"].id] = true,	}local captureAbleUnits = {	[UnitDefNames["tpoilpump"].id] = true,}	function widget:DefaultCommand()	local sU = Spring.GetSelectedUnits()	if (sU and #sU==1) then		local ud = Spring.GetUnitDefID (sU[1])		if (autoCaptureUnits[ud]) then				local mx,my=Spring.GetMouseState()			local s,t =Spring.TraceScreenRay(mx,my)			if s == "unit" then						local cu = Spring.GetUnitDefID (t)				if (captureAbleUnits[cu]) then					return CMD.CAPTURE,t				end			end		end	endend